        <th scope="col" rowspan="2">Thấp</th>
      </tr>
      <tr>
        <th scope="col">Giá 3</th>
        <th scope="col">KL 3</th>
        <th scope="col">Giá 2</th>
        <th scope="col">KL 2</th>
        <th scope="col">Giá 1</th>
        <th scope="col">KL 1</th>

        <th scope="col">Giá</th>
        <th scope="col">KL</th>
        <th scope="col">+/-</th>

        <th scope="col">Giá 1</th>
        <th scope="col">KL 1</th>
        <th scope="col">Giá 2</th>
        <th scope="col">KL 2</th>
        <th scope="col">Giá 3</th>
        <th scope="col">KL 3</th>
