        <td class="status-{{stock.g1[2]}}" [ngClass]="{'bg-decrease': stock.g1[1] < currentStocks[i].g1[1], 'bg-increase': stock.g1[1] > currentStocks[i].g1[1]}">{{ stock.g1[1] }}</td>
        <td class="status-{{stock.g2[2]}}" [ngClass]="{'bg-decrease': stock.g2[0] < currentStocks[i].g2[0], 'bg-increase': stock.g2[0] > currentStocks[i].g2[0]}">{{ stock.g2[0] }}</td>
        <td class="status-{{stock.g2[2]}}" [ngClass]="{'bg-decrease': stock.g2[1] < currentStocks[i].g2[1], 'bg-increase': stock.g2[1] > currentStocks[i].g2[1]}">{{ stock.g2[1] }}</td>
        <td class="status-{{stock.g3[2]}}" [ngClass]="{'bg-decrease': stock.g3[0] < currentStocks[i].g3[0], 'bg-increase': stock.g3[0] > currentStocks[i].g3[0]}">{{ stock.g3[0] }}</td>
        <td class="status-{{stock.g3[2]}}" [ngClass]="{'bg-decrease': stock.g3[1] < currentStocks[i].g3[1], 'bg-increase': stock.g3[1] > currentStocks[i].g3[1]}">{{ stock.g3[1] }}</td>

        <td class="{{stock.statusColor}}" [ngClass]="{'bg-decrease': stock.lastPrice !== currentStocks[i].lastPrice}">{{ stock.lastPrice }}</td>
        <td class="{{stock.statusColor}}" [ngClass]="{'bg-decrease': stock.lastVolume !== currentStocks[i].lastVolume}">{{ stock.lastVolume }}</td>
        <td class="{{stock.statusColor}}" [ngClass]="{'bg-decrease': stock.ot !== currentStocks[i].ot}">{{ stock.ot }}</td>

        <td class="status-{{stock.g4[2]}}" [ngClass]="{'bg-decrease': stock.g4[0] < currentStocks[i].g4[0], 'bg-increase': stock.g4[0] > currentStocks[i].g4[0]}">{{ stock.g4[0] }}</td>
        <td class="status-{{stock.g4[2]}}" [ngClass]="{'bg-decrease': stock.g4[1] < currentStocks[i].g4[1], 'bg-increase': stock.g4[1] > currentStocks[i].g4[1]}">{{ stock.g4[1] }}</td>
        <td class="status-{{stock.g5[2]}}" [ngClass]="{'bg-decrease': stock.g5[0] < currentStocks[i].g5[0], 'bg-increase': stock.g5[0] > currentStocks[i].g5[0]}">{{ stock.g5[0] }}</td>
        <td class="status-{{stock.g5[2]}}" [ngClass]="{'bg-decrease': stock.g5[1] < currentStocks[i].g5[1], 'bg-increase': stock.g5[1] > currentStocks[i].g5[1]}">{{ stock.g5[1] }}</td>
        <td class="status-{{stock.g6[2]}}" [ngClass]="{'bg-decrease': stock.g6[0] < currentStocks[i].g6[0], 'bg-increase': stock.g6[0] > currentStocks[i].g6[0]}">{{ stock.g6[0] }}</td>
        <td class="status-{{stock.g6[2]}}" [ngClass]="{'bg-decrease': stock.g6[1] < currentStocks[i].g6[1], 'bg-increase': stock.g6[1] > currentStocks[i].g6[1]}">{{ stock.g6[1] }}</td>

        <td>{{ stock.lot }}</td>
        <td>0</td>
        <td>0</td>
        <td>{{ stock.avePrice }}</td>
        <td>{{ stock.highPrice }}</td>
        <td>{{ stock.lowPrice }}</td>

      </tr>
    </tbody>
  </table>
</div>


