                <app-sym-candlechart></app-sym-candlechart>
              </div>
            </ng-template>

          </li>
          <li class="nav-item" ngbNavItem="profile-settings"><a href="#profile-settings" data-toggle="tab"
              class="nav-link" ngbNavLink>Tài chính</a>
            <ng-template ngbNavContent>
              <app-sym-finance></app-sym-finance>
            </ng-template>
          </li>
        </ul>

        <div [ngbNavOutlet]="nav" class=""></div>
      </div>
      <!-- Modal -->
    </div>
  </div>
</ng-template>
